import "./rightbar.css";
import { useEffect, useState, useContext, useRef } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { AuthContext } from "../../context/AuthContext";
import { Add, Remove } from "@material-ui/icons";
import { useNavigate } from "react-router-dom";

export default function Rightbar({ user }) {
    const username = useRef();
    const email = useRef();
    const city = useRef();
    const from = useRef();
    const profilePic = useRef();
    const coverPic = useRef();
    const password = useRef();
    const passwordAgain = useRef();
    const editModal = useRef();
    const editBtn = useRef();
    const editClose = useRef();
    const [profile, setProfile] = useState(null);
    const [cover, setCover] = useState(null);
    const PF = process.env.REACT_APP_PUBLIC_FOLDER;
    const [friends, setFriends] = useState([]);
    const { user: currentUser, dispatch } = useContext(AuthContext);
    const [followed, setFollowed] = useState(
        currentUser.followins.includes(user?._id)
    );
    const navigate = useNavigate();

    useEffect(() => {
        const checkFollowed = async () => {
            try {
                const res = currentUser.followins.includes(user?._id);
                setFollowed(res);
            } catch (err) {
                console.log(err);
            }
        };
        checkFollowed();
    }, [user]);

    useEffect(() => {
        const getFriends = async () => {
            try {
                const friendList = await axios.get(
                    "/users/friends/" + user._id
                );
                setFriends(friendList.data);
            } catch (err) {
                console.log(err);
            }
        };
        getFriends();
    }, [user]);

    const handleCLick = async () => {
        try {
            if (followed) {
                await axios.put(`/users/${user._id}/unfollow`, {
                    userId: currentUser._id,
                });
                dispatch({ type: "UNFOLLOW", payload: user._id });
            } else {
                await axios.put(`/users/${user._id}/follow`, {
                    userId: currentUser._id,
                });
                dispatch({ type: "FOLLOW", payload: user._id });
            }
            console.log("ha");
            setFollowed(!followed);
        } catch (err) {}
    };

    const handleCLickMessage = async () => {
        const valid = await axios.get(
            "/conversations/find/" + currentUser._id + "/" + user._id
        );
        try {
            if (valid.data == null)
                await axios.post("/conversations/", {
                    senderId: currentUser._id,
                    receiverId: user._id,
                });
        } catch (err) {
            console.log(err);
        }
        navigate("/messenger");
    };

    const handleClickEdit = () => {
        editBtn.current.onclick = function () {
            editModal.current.style.display = "block";
        };

        editClose.current.onclick = function () {
            editModal.current.style.display = "none";
        };

        window.onclick = function (event) {
            if (event.target === editModal.current) {
                editModal.current.style.display = "none";
            }
        };
    };

    const handleSubmitEdit = async () => {

        if (profile) {
            const data = new FormData();
            const fileName = Date.now() + file.name;
            data.append("name", fileName);
            data.append("file", file);
            newPost.img = fileName;
            console.log(newPost);
            try {
                await axios.post("/upload", data);
            } catch (err) {
                console.log(err);
            }
        }
        try {
            await axios.post("/posts", newPost);
            window.location.reload();
        } catch (err) {}
    };

    const HomeRightbar = () => {
        return (
            <>
                <div className="birthdayContainer">
                    <img className="birthdayImg" src="assets/gift.png" alt="" />
                    <span className="birthdayText">
                        <b>Bao Hang</b> and <b>3 other friends</b> have a
                        birthday today.
                    </span>
                </div>
                <img className="rightbarAd" src="assets/ad.png" alt="" />
            </>
        );
    };

    const ProfileRightbar = () => {
        return (
            <>
                {user.username !== currentUser.username && (
                    <div className="barButtons">
                        <button
                            className="rightbarFollowButton"
                            onClick={handleCLick}
                        >
                            {followed ? "Unfollow" : "Follow"}
                            {followed ? <Remove /> : <Add />}
                        </button>
                        <button
                            className="rightbarFollowButton"
                            onClick={handleCLickMessage}
                        >
                            Go to Chat
                        </button>
                    </div>
                )}

                {user.username === currentUser.username && (
                    <div className="barButtons">
                        <button
                            className="rightbarEditButton"
                            onClick={handleClickEdit}
                            ref={editBtn}
                        >
                            Edit profile
                        </button>

                        <div class="rightbarEditContainer">
                            <div id="myModal" class="modal" ref={editModal}>
                                <div class="modal-content">
                                    <h2>Profile</h2>

                                    <form onSubmit={handleSubmitEdit}>
                                        <label for="username">User name:</label>
                                        <br />
                                        <input
                                            type="text"
                                            id="username"
                                            name="username"
                                            value="John"
                                            ref={username}
                                        />
                                        <br />
                                        <label for="email">Email:</label>
                                        <br />
                                        <input
                                            type="text"
                                            id="email"
                                            name="email"
                                            value="Doe"
                                            ref={email}
                                        />
                                        <br />
                                        <label for="city">City:</label>
                                        <br />
                                        <input
                                            type="text"
                                            id="city"
                                            name="city"
                                            value="Doe"
                                            ref={city}
                                        />
                                        <br />
                                        <label for="from">From:</label>
                                        <br />
                                        <input
                                            type="text"
                                            id="from"
                                            name="from"
                                            value="Doe"
                                            ref={from}
                                        />
                                        <br />
                                        <label for="password">Password:</label>
                                        <br />
                                        <input
                                            type="text"
                                            id="password"
                                            name="password"
                                            value="Doe"
                                            ref={password}
                                        />
                                        <br />
                                        <label for="passwordAgain">
                                            Password again:
                                        </label>
                                        <br />
                                        <input
                                            type="text"
                                            id="passwordAgain"
                                            name="passwordAgain"
                                            value="Doe"
                                            ref={passwordAgain}
                                        />
                                        <br />
                                        <label for="profilePic">
                                            Profile picture:
                                        </label>
                                        <br />
                                        <input
                                            type="file"
                                            id="profilePic"
                                            name="profilePic"
                                            // value="Doe"
                                            onChange={(e) => setProfile(e.target.files[0])}
                                            accept=".png,.jpeg,.jpg"
                                            ref={profilePic}
                                        />
                                        <br />
                                        <label for="coverPic">
                                            Cover picture:
                                        </label>
                                        <br />
                                        <input
                                            type="file"
                                            id="coverPic"
                                            name="coverPic"
                                            // value="Doe"
                                            accept=".png,.jpeg,.jpg"
                                            onChange={(e) => setCover(e.target.files[0])}
                                            ref={coverPic}
                                        />
                                        <br />
                                        <input type="submit" value="Submit" />
                                    </form>
                                    <button ref={editClose} className="close">
                                        x
                                    </button>
                                </div>
                            </div>
                        </div>
                    </div>
                )}

                <h4 className="rightbarTitle">User information</h4>
                <div className="rightbarInfo">
                    <div className="rightbarInfoItem">
                        <span className="rightbarInfoKey">City:</span>
                        <span className="rightbarInfoValue">{user.city}</span>
                    </div>
                    <div className="rightbarInfoItem">
                        <span className="rightbarInfoKey">From:</span>
                        <span className="rightbarInfoValue">{user.from}</span>
                    </div>
                    <div className="rightbarInfoItem">
                        <span className="rightbarInfoKey">Relationship:</span>
                        <span className="rightbarInfoValue">
                            {user.relationship === 1
                                ? "Single"
                                : user.relationship === 2
                                ? "Married"
                                : "-"}
                        </span>
                    </div>
                </div>
                <h4 className="rightbarTitle">User friends</h4>
                <div className="rightbarFollowings">
                    {friends.map((friend) => (
                        <Link
                            to={"/profile/" + friend.username}
                            style={{ textDecoration: "none" }}
                        >
                            <div className="rightbarFollowing">
                                <img
                                    src={
                                        friend.profilePicture
                                            ? PF + friend.profilePicture
                                            : PF + "person/noAvatar.png"
                                    }
                                    alt=""
                                    className="rightbarFollowingImg"
                                />
                                <span className="rightbarFollowingName">
                                    {friend.username}
                                </span>
                            </div>
                        </Link>
                    ))}
                </div>
            </>
        );
    };
    return (
        <div className="rightbar">
            <div className="rightbarWrapper">
                {user ? <ProfileRightbar /> : <HomeRightbar />}
            </div>
        </div>
    );
}
