import "./rightbar.css";
import { useEffect, useState, useContext } from "react";
import { Link } from "react-router-dom";
import axios from "axios";
import { AuthContext } from "../../context/AuthContext";
import { Add, Remove } from "@material-ui/icons";
import { useNavigate } from "react-router-dom";

export default function Rightbar({ user }) {
    const PF = process.env.REACT_APP_PUBLIC_FOLDER;
    const [friends, setFriends] = useState([]);
    const { user: currentUser, dispatch } = useContext(AuthContext);
    const [followed, setFollowed] = useState(
        currentUser.followins.includes(user?._id)
    );
    const navigate = useNavigate();

    useEffect(() => {
        const checkFollowed = async () => {
            try {
                const res = currentUser.followins.includes(user?._id);
                setFollowed(res);
            } catch (err) {
                console.log(err);
            }
        };
        checkFollowed();
    }, [user]);

    useEffect(() => {
        const getFriends = async () => {
            try {
                const friendList = await axios.get(
                    "/users/friends/" + user._id
                );
                setFriends(friendList.data);
            } catch (err) {
                console.log(err);
            }
        };
        getFriends();
    }, [user]);

    const handleCLick = async () => {
        try {
            if (followed) {
                await axios.put(`/users/${user._id}/unfollow`, {
                    userId: currentUser._id,
                });
                dispatch({ type: "UNFOLLOW", payload: user._id });
            } else {
                await axios.put(`/users/${user._id}/follow`, {
                    userId: currentUser._id,
                });
                dispatch({ type: "FOLLOW", payload: user._id });
            }
            console.log("ha");
            setFollowed(!followed);
        } catch (err) {}
    };

    const handleCLickMessage = async () => {
        const valid = await axios.get(
            "/conversations/find/" + currentUser._id + "/" + user._id
        );
        try {
            if (valid.data == null)
                await axios.post("/conversations/", {
                    senderId: currentUser._id,
                    receiverId: user._id,
                });
        } catch (err) {
            console.log(err);
        }
        navigate("/messenger");
    };

    const handleClickEdit = () => {
        let modal = document.getElementById("myModal");

        let btn = document.querySelector(".rightbarEditButton");

        var span = document.getElementsByClassName("close")[0];

        btn.onclick = function () {
            modal.style.display = "block";
        };

        span.onclick = function () {
            modal.style.display = "none";
        };

        window.onclick = function (event) {
            if (event.target == modal) {
                modal.style.display = "none";
            }
        };
    };

    const HomeRightbar = () => {
        return (
            <>
                <div className="birthdayContainer">
                    <img className="birthdayImg" src="assets/gift.png" alt="" />
                    <span className="birthdayText">
                        <b>Bao Hang</b> and <b>3 other friends</b> have a
                        birthday today.
                    </span>
                </div>
                <img className="rightbarAd" src="assets/ad.png" alt="" />
            </>
        );
    };

    const ProfileRightbar = () => {
        return (
            <>
                {user.username !== currentUser.username && (
                    <div className="barButtons">
                        <button
                            className="rightbarFollowButton"
                            onClick={handleCLick}
                        >
                            {followed ? "Unfollow" : "Follow"}
                            {followed ? <Remove /> : <Add />}
                        </button>
                        <button
                            className="rightbarFollowButton"
                            onClick={handleCLickMessage}
                        >
                            Go to Chat
                        </button>
                    </div>
                )}

                {user.username === currentUser.username && (
                    <div className="barButtons">
                        <button
                            className="rightbarEditButton"
                            onClick={handleClickEdit}
                        >
                            Edit profile
                        </button>

                        <div class="rightbarEditContainer">
                            <div id="myModal" class="modal">
                                <div class="modal-content">
                                <h2>HTML Forms</h2>

<form action="/action_page.php">
  <label for="fname">First name:</label><br/>
  <input type="text" id="fname" name="fname" value="John" /><br/>
  <label for="lname">Last name:</label><br/>
  <input type="text" id="lname" name="lname" value="Doe" /><br/><br/>
  <input type="submit" value="Submit" />
</form> 
                                </div>
                            </div>
                        </div>
                    </div>
                )}

                <h4 className="rightbarTitle">User information</h4>
                <div className="rightbarInfo">
                    <div className="rightbarInfoItem">
                        <span className="rightbarInfoKey">City:</span>
                        <span className="rightbarInfoValue">{user.city}</span>
                    </div>
                    <div className="rightbarInfoItem">
                        <span className="rightbarInfoKey">From:</span>
                        <span className="rightbarInfoValue">{user.from}</span>
                    </div>
                    <div className="rightbarInfoItem">
                        <span className="rightbarInfoKey">Relationship:</span>
                        <span className="rightbarInfoValue">
                            {user.relationship === 1
                                ? "Single"
                                : user.relationship === 2
                                ? "Married"
                                : "-"}
                        </span>
                    </div>
                </div>
                <h4 className="rightbarTitle">User friends</h4>
                <div className="rightbarFollowings">
                    {friends.map((friend) => (
                        <Link
                            to={"/profile/" + friend.username}
                            style={{ textDecoration: "none" }}
                        >
                            <div className="rightbarFollowing">
                                <img
                                    src={
                                        friend.profilePicture
                                            ? PF + friend.profilePicture
                                            : PF + "person/noAvatar.png"
                                    }
                                    alt=""
                                    className="rightbarFollowingImg"
                                />
                                <span className="rightbarFollowingName">
                                    {friend.username}
                                </span>
                            </div>
                        </Link>
                    ))}
                </div>
            </>
        );
    };
    return (
        <div className="rightbar">
            <div className="rightbarWrapper">
                {user ? <ProfileRightbar /> : <HomeRightbar />}
            </div>
        </div>
    );
}
