import "./message.css";
import {format} from "timeago.js";
import {useEffect, useState} from "react";
import axios from "axios";

export default function Message({message,own}) {
    const PF = process.env.REACT_APP_PUBLIC_FOLDER;
    const [sender, setSender] = useState();
    const [other, setOther] = useState();

    useEffect(()=>{
        const fetchConversation = async () => {
            try{
                const senderUser = await axios.get("/users?userId="+message.sender);
                setSender(senderUser.data);
                const conversation = await axios.get("/conversations?convId="+message.conversationId);
                const otherId = conversation.data.members.filter((i)=> i!==sender._id);
                const otherUser = await axios.get("/users?userId="+otherId);
                setOther(otherUser.data);
            } catch (err){
                console.log(err);
            }
        };
        fetchConversation();
    },[]);

    return (
        <div className={own ? "message own" : "message"}>
            <div className="messageTop">
                <img
                    src={own ? (sender.profilePicture ?PF+sender.profilePicture :PF+"person.noAvatar.png"): (other?.profilePicture ?PF+other.profilePicture :PF+"person.noAvatar.png")}
                    alt=""
                    className="messageImg"
                />
                <p className="messageText">
                    {message.text}
                </p>
            </div>
            <div className="messageBottom">{format(message.createdAt)}</div>
        </div>
    );
}
